def send_serial_data(port, baudrate=9600):
    # Convertir el string hexadecimal a bytes
    hex_bytes = []
    for line in hex_input.split('\n'):
        for byte in line.strip().split():
            try:
                hex_bytes.append(int(byte, 16))
            except ValueError:
                continue

    data = bytes(hex_bytes)

    try:
        # Configurar puerto serial
        ser = serial.Serial(port, baudrate, timeout=1)
        time.sleep(2)  # Esperar a que se establezca la conexión

        # Enviar datos
        print(f"Enviando {len(data)} bytes por el puerto {port}...")
        ser.write(data)
        print("Datos enviados correctamente.")

    except serial.SerialException as e:
        print(f"Error al abrir el puerto serial: {e}")
    finally:
        if 'ser' in locals() and ser.is_open:
            ser.close()


